INSERT INTO webtoon_tb (`author`, `age`,`title`,`star_count`,`week_day`,`hashtag`,`intro`,`like_count`,`specially`,`image`,`created_at`) VALUES  ('갬쟈',0, '개같이 탈출', 9.95,'월','#드라마#학원물#먼치킨','"살아남으라고? 나는 자그만 말티즈란 말이야!"세상 따뜻하고 포근한 가정집에서 살아온 삐용이. 언니 품에서 그저 귀엽기만 하면 되는 견생이었는데... 갑자기 가족들과 모든 인간이 사라져버렸다.혼자서는 아무것도 할 줄 모르는데, 괴물 좀비 개들이 우글거리는 고립된 도시를 탈출해 언니를 만날 수 있을까?',930871, '완결','thumbnail_IMAG21_8746b12b-566e-4375-b0ab-1eaf48e17a1d.jpg',now());
