INSERT INTO webtoon_tb (`author`, `age`,`title`,`star_count`,`week_day`,`hashtag`,`intro`,`like_count`,`specially`,`image`,`created_at`) VALUES  ('밋츄 / 하티 / 이흰',0, '악당 가족이 독립을 반대한다', 9.92,'일','#드라마#학원물#먼치킨','가족애 따위 없는 살벌한 악당 가문의 사생아 엘로디, 어느 날 갑자기 전생의 기억을 찾다!자신이 환생한 이곳이 전생에 읽었던 소설 속이며3년 후 진짜 여주인공이 나타나면서 자신은 악당 가족에게 버림받고 살해당할 운명임을 깨닫고언니가 등장하기 전까지 적당히 뒷돈 챙기다가 이 가문에서 독립하자!고 생각했는데……“독립? 그게 무슨 개소리지?”“너한테 바람 불어 넣은 놈이 누구야.”“누님, 혹시라도 가출할 생각은 아니겠지?”가족들이 독립을 반대한다? 저 살고 싶은데요, 독립시켜 주세요!',579166, '','thumbnail_IMAG21_fa059a74-ca14-4a89-b0d7-841a0c85139a.jpg',now());
INSERT INTO webtoon_tb (`author`, `age`,`title`,`star_count`,`week_day`,`hashtag`,`intro`,`like_count`,`specially`,`image`,`created_at`) VALUES  ('빤쓰',12, '히어로메이커', 9.95,'수','#드라마#학원물#먼치킨','왕은 영웅이 되고 싶어하는 공주의 소원을 들어주기로 전격 결심! 공주를 속이고 마치 영웅이 된 것처럼 만들기 위해 온 나라가 연극을 하게 되는데..파란만장한 그들만의 눈물겨운 영웅만들기의 대장정이 펼쳐집니다~',254914, '','thumbnail_IMAG21_7292511306663934265.jpg',now());
INSERT INTO webtoon_tb (`author`, `age`,`title`,`star_count`,`week_day`,`hashtag`,`intro`,`like_count`,`specially`,`image`,`created_at`) VALUES  ('황지성 / 스튜디오MW / 이블라인',0, '천재 타자가 강속구를 숨김', 9.91,'금','#드라마#학원물#먼치킨','부와 명예, 모든 걸 얻은 천재 메이저리거 강건우.그런 그조차도 아내와의 행복한 결혼 생활만큼은 이어갈 수 없었다.아내의 빈자리를 느끼며 후회하던 그가 신비한 반지의 힘으로 과거에 돌아왔다.풋풋한 연애시절의 고등학생이 된 그는 이번에야말로 사랑을 쟁취할 수 있을까.',244007, '순위','thumbnail_IMAG21_91cb8ff9-5be3-40ba-8df9-661972577cd2.jpg',now());
INSERT INTO webtoon_tb (`author`, `age`,`title`,`star_count`,`week_day`,`hashtag`,`intro`,`like_count`,`specially`,`image`,`created_at`) VALUES  ('규남',12, '신군', 9.95,'수','#드라마#학원물#먼치킨','악마에게 모든 걸 잃고 먹이로 사육당하는 소년.이를 갈며 복수를 갈망하지만 인간의 힘은 악마에게 닿지 못했다.그때 그의 앞에 신군이 나타난다.2020 지상최대공모전 2기 장려상 수상작!',278978, '무료','thumbnail_IMAG21_ca9a662b-7a79-4e80-94c9-d62ebe597aad.jpg',now());
