INSERT INTO webtoon_tb (`author`, `age`,`title`,`star_count`,`week_day`,`hashtag`,`intro`,`like_count`,`specially`,`image`,`created_at`) VALUES  ('신건 / taibogi',0, '랭커', 9.86,'토','#드라마#학원물#먼치킨','극비리로 진행되는 멤버쉽 온라인 게임 <랭커>.우연히 랭커에 뛰어든 격투기선수 강준서.어느 순간부터 상대가 괴물로 보였던 준서는 자신의 플레이어인 정준의 목소리를 통해서 안정을 되찾고,정준과 함께하면서 증세를 치료하고자 마음먹는다.하지만 랭커에는 목숨을 걸어야 하는 퀘스트뿐 아니라,캐릭터끼리 벌이는 배틀과 사냥의 위협까지 도사리고 있다.',237405, '','thumbnail_IMAG21_46875d4a-190f-4663-bac5-0d3003263646.jpg',now());
INSERT INTO webtoon_tb (`author`, `age`,`title`,`star_count`,`week_day`,`hashtag`,`intro`,`like_count`,`specially`,`image`,`created_at`) VALUES  ('삵 / 쏘가리 / 임하얌',15, '악당과 악당이 만나면', 9.95,'목','#드라마#학원물#먼치킨','귀족들을 상대로 악명을 떨치던 제국 뒷세계 조직의 보스, 아탈란테. 어느 날 황제로부터 폭군 리오넬 비안크 대공을 암살해 달라는 의뢰를 받게 된다.조직원들과 평범하게 살고 싶었던 아탈란테는 의뢰를 수락하고 대공성에 잠입해 암살 시도하는데….하지만 어째서인지 죽지 않아?한순간에 사랑을 속삭이는 대공에게서 아탈란테는 무사히 탈출할 수 있을까?',978839, '무료','thumbnail_IMAG21_f09967ec-e4e8-4fb8-a8d5-a65db4c6e14e.jpg',now());
INSERT INTO webtoon_tb (`author`, `age`,`title`,`star_count`,`week_day`,`hashtag`,`intro`,`like_count`,`specially`,`image`,`created_at`) VALUES  ('김이랑',8, '하루의 하루', 9.92,'화','#드라마#학원물#먼치킨','오하루는 다른 사람의 마음을 읽을 수 있는 초능력자다.얼마 전, 저주 받은 능력 때문에 첫사랑도 허무하게 깨져버리고건조한 일상을 보내고 있던 하루에게 최근 신경쓰이는 사람이 생겼다.​그 사람은 바로, 같은 과 동기인 도하루.그녀의 마음은 도무지 보이지 않는다. 이건 혹시.. 운명..?',720622, '무료','thumbnail_IMAG21_4121973654889457249.jpg',now());
INSERT INTO webtoon_tb (`author`, `age`,`title`,`star_count`,`week_day`,`hashtag`,`intro`,`like_count`,`specially`,`image`,`created_at`) VALUES  ('박혬',8, '백호랑', 9.95,'화','#드라마#학원물#먼치킨','모든 것이 완벽해 보이는 지룡은 비밀스런 동거 중!사촌동생이자 고양이이자 수호자인 소년은 바로 서쪽의 신수 백호였다!과거의 죄를 씻기 위해 인간계에서 하늘의 심부름을 수행인 백호랑과 그에게 엮인 지룡의 스펙타클한 일상',759318, '순위','thumbnail_IMAG21_3978989878055821874.jpg',now());
INSERT INTO webtoon_tb (`author`, `age`,`title`,`star_count`,`week_day`,`hashtag`,`intro`,`like_count`,`specially`,`image`,`created_at`) VALUES  ('알깨 / 새몽',0, '최후의 금빛아이', 9.95,'월','#드라마#학원물#먼치킨','언니의 생일날, 이 세계를 지배하는 신에 의해 모든 것을 잃은 소녀 록시.자신이 신들의 먹이에 불과하다는 충격적인 진실을 알게 된다.록시는 심장만 있으면 언니를 되살릴 수 있다는 말을 믿고 험난한 여행을 떠난다.그러나 곧 그녀를 뒤쫓는 자들과 감당할 수 없는 적들이 나타나는데...록시는 앞을 막아서는 그들을 물리치고 과연 언니를 되살릴 수 있을까?',705583, '무료','thumbnail_IMAG21_fdecfea5-8ca1-4a32-9c55-34a30f24266d.jpg',now());
INSERT INTO webtoon_tb (`author`, `age`,`title`,`star_count`,`week_day`,`hashtag`,`intro`,`like_count`,`specially`,`image`,`created_at`) VALUES  ('늠개',0, '고백어택', 9.85,'일','#드라마#학원물#먼치킨','하나는 언제나 2등이었다. 늘 1등을 하는 한건우 때문에!속을 알 수 없는 건우를 무너뜨리기 위해 수단과 방법을 가리지 않던 하나.이번에는 가짜 고백으로 멘탈을 흔들려 했는데...어라, 얘 이 고백 왜 받지? 뭐... 내가 좋다고?!',54331, '','thumbnail_IMAG21_d0a7cee2-4ded-4506-aedf-71c25675247c.jpg',now());
